import fsExtra = require('fs-extra')
import path = require('path')

export class Assets {

    static pulseaudioService(): string {
        return this.readFile('pulseaudio.service')
    }

    static pulseaudioAsoundConf(): string {
        return this.readFile('pulse_asound.conf')
    }

    static pulseaudioAecService(): string {
        return this.readFile('pulseaudio-aec.service')
    }

    static pulseaudioSnipsAudioServerService(): string {
        return this.readFile('pulseaudio_snips-audio-server.service')
    }

    static pgpKey(): string {
        return this.readFile('snips-pgp-key.asc')
    }

    static homeAssistantPythonSystemdService(): string {
        return this.readFile('home-assistant.service')
    }

    private static readFile(name: string): string {
        return fsExtra.readFileSync(path.join(__dirname, name), 'utf8')
    }
}
